let name = "Nick"
let age = 33

// Concatenation
// You can concat without string interpolation:
console.log("My name is " + name + " and I am " + age)

// In ES6, we can a Template String (string interpolation in Ruby). Use this instead.
console.log(`My Name is ${name} and I am ${age}`)

